    for(let r = 0; r < rows; r++) {
        for(let c = 0; c < columns; c++) {
            const key = getKey(r, c)
            if(grid[r][c] && !visited.has(key)) {
                islands++
                dfs(grid, r, c, visited)
            }
        }
    }

    return islands
}

function dfs(grid, r, c, visited) {
    const dir = [[1, 0], [-1, 0], [0, 1], [0, -1]]
    if(isValid(grid, r, c, visited)) {
        const key = getKey(r, c)
        visited.add(key)
        for(const [dx, dy] of dir) {
            dfs(grid, r + dx, c + dy, visited)
        }
    }
}

function isValid(grid, r, c, visited) {
    const key = getKey(r, c)
    if(r >= 0 && c >= 0 && r < grid.length && c < grid[0].length && grid[r][c] && !visited.has(key)) return true

     return false
}
