class Solution {
public:

    void dfs(int i,int j,int m,int n,vector<vector<int>> &grid,int color){
        if(i < 0 or j < 0 or i >= m or j >= n or grid[i][j] == 0 or grid[i][j] == color){
            return;
        }
        grid[i][j] = color;
        dfs(i+1,j,m,n,grid,color);
        dfs(i,j-1,m,n,grid,color);
        dfs(i-1,j,m,n,grid,color);
        dfs(i,j+1,m,n,grid,color);
    }

    int countOnes(int m,int n,vector<vector<int>> &grid){
        int ones = 0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j] == 1){
                    ones++;
                }
            }
        }
        return ones;
    }

    int countIslands(int m,int n,vector<vector<int>> &grid){
        int island = 0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j] == 1){
                    dfs(i,j,m,n,grid,2);
                    island++;
                }
            }
        }        
        return island;
    }

    int minDays(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int ones = countOnes(m,n,grid);

        int island = countIslands(m,n,grid);


        if(island != 1) return 0; //1st case 

        if(m == 1 or n == 1 or ones <= 2){
            return ones == 2 ? 2:1; //2nd case
        }

        //Last option
        int islandColor = 2;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j] == islandColor){
                    grid[i][j] = 0;
                    island = 0;
                    for(int k=0;k<m;k++){
                        for(int l=0;l<n;l++){
                            if(grid[k][l] == islandColor){
                                dfs(k,l,m,n,grid,islandColor+1);
                                island++;
                            }
                        }
                    }
                    if(island > 1) return 1; // 3rd case
                    grid[i][j] = ++islandColor;
                }
            }
        }
        return 2; //4th case

    }
};

