class Solution {
    public int minDays(int[][] grid) {
        int countOne = 0;
        for(int i=0;i<grid.length;i++){
            for(int j=0;j<grid[0].length;j++){
                if(grid[i][j] == 1){
                    countOne++;
                }
            }
        }
        if(countOne == 1) return 1;
       int k = countIsland(grid);
        if(k > 1) return 0;
        for(int i=0;i<grid.length;i++){
            for(int j=0;j<grid[0].length;j++){
                if(grid[i][j] == 1){
                    grid[i][j] = 0;
                    int g = countIsland(grid);
                    if(g>1) return 1;
                    grid[i][j] = 1;
                }
            }
        }
        return 2;
    }
    int countIsland(int grid[][]){
        int c=0;
         boolean v[][] = new boolean[grid.length][grid[0].length];  
          for(int i=0;i<grid.length;i++){
            for(int j=0;j<grid[i].length;j++){
                if(grid[i][j] == 1 && v[i][j] == false){
                    ++c;
                   count(grid,i,j,v);
                }
            }
        }
        return c;
    }
    void count(int g[][],int i,int j,boolean v[][]){
        if(i<0 || j<0 || i>=g.length || j>=g[0].length || g[i][j] == 0 || v[i][j]){
            return;
        }
        v[i][j] = true;
        count(g,i+1,j,v);
        count(g,i,j+1,v);
        count(g,i-1,j,v);
        count(g,i,j-1,v);
        return;
    }
}
