class Solution {

    int[] dir = {1,0,-1,0,1}; // dir to move in grid

    public void dfs(int[][] grid , int r , int c , boolean[][] visited){
        if(r<0 || c<0 || r>=grid.length || c>=grid[0].length || grid[r][c]== 0 || visited[r][c]) // condition to not move to grid
            return ;
        visited[r][c] = true;

        for(int i=0;i<4;i++){
            dfs(grid , r+dir[i] , c+dir[i+1] ,visited);
        }
    }

    public int countIsland(int[][] grid){
        int row = grid.length;
