        set<pair<int,int>> visited;
        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[0].size();j++)
            {
                if(grid[i][j] ==1)
                {
                    st.push({i,j});
                    count--;
                    j=INT_MAX-1;
                    i= INT_MAX-1;
                }
            }
        }

        while(!st.empty())
        {
            pair<int,int> coordinate = st.top();
            st.pop();
            visited.insert(coordinate);

            int x_cor = coordinate.first,y_cor=coordinate.second;
            for(int i=0;i<4;i++)
            {
                if(x_cor + X[i]<grid.size() && x_cor+X[i]>=0 && y_cor + Y[i]<grid[0].size() && y_cor+Y[i] >=0)
                {
                    if(visited.find({x_cor + X[i],y_cor+Y[i]})==visited.end() && grid[x_cor + X[i]][y_cor+Y[i]]==1)
                    {
                        count--;
                        visited.insert({x_cor + X[i],y_cor+Y[i]});
                        st.push({x_cor + X[i],y_cor+Y[i]});
                    }
                }
            }


        }

        if(count == 0)
            return false;
        return true;
    }
    int minDays(vector<vector<int>>& grid) {
        set<pair<int,int>> s;
