        // if not flip every one bit to 0 and check for each combination if disconnected else return 2;
        int count=0;
        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[0].size();j++)
            {
                if(grid[i][j] ==1)
                    count++;
            }
        }        


        if(count==0 || disconnected(grid,count))
            return 0;

        if(count == 1)
            return 1;

        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[0].size();j++)
            {
                if(grid[i][j])
                {
                    grid[i][j] = 0;
                        if(disconnected(grid,count-1))
                        {   
                            return 1;
                        }
                    grid[i][j] = 1;
                }
