	}




/*************************Utilities*******************************/
public class ArticulationPoints {

private static int time;

// The function to do DFS traversal. It uses recursive function APUtil() 
public static List<Integer> getAP(HashMap<Integer, List<Integer>> adjacencyList) {

	// Initialize parent and visited, and ap(articulation point) arrays
	List<Integer> ap = new ArrayList();
	HashMap<Integer, Integer> disc = new HashMap<Integer, Integer>(); // vertex, discovery value
	HashMap<Integer, Integer> low = new HashMap<Integer, Integer>(); // vertex, low value
	HashMap<Integer, Integer> parent = new HashMap<Integer, Integer>(); // // child vertex, parent vertex

	Set<Integer> visited = new HashSet<Integer>();

	// Call the recursive helper function to find articulation 
	// points in DFS tree

	for (Map.Entry<Integer, List<Integer>> entry : adjacencyList.entrySet()) {
		int v = entry.getKey();
		if (!disc.containsKey(v))
			APUtil(v, visited, disc, low, parent, ap, adjacencyList);
	}
	return ap;
}

// A recursive function that find articulation points using DFS 
// u --> The vertex to be visited next 
// visited[] --> keeps tract of visited vertices 
// disc[] --> Stores discovery times of visited vertices 
// parent[] --> Stores parent vertices in DFS tree 
// ap[] --> Store articulation points 
public static void APUtil(int currVertex, Set<Integer> visited, HashMap<Integer, Integer> disc,
		HashMap<Integer, Integer> low, HashMap<Integer, Integer> parent, List<Integer> ap,
		HashMap<Integer, List<Integer>> adjacencyList) {

	// Count of children in DFS Tree 
	int children = 0;

	// Mark the current node as visited 
	visited.add(currVertex);

	// Initialize discovery time and low value

	disc.put(currVertex, time);
	low.put(currVertex, time);
	++time;

	int neighbor;

	if (adjacencyList.containsKey(currVertex)) {
		// Go through all vertices adjacent to this  
		Iterator<Integer> itr = adjacencyList.get(currVertex).iterator();
		while (itr.hasNext()) {

			neighbor = itr.next();
//				//if neighbor is same as parent then just ignore this vertex.
//	            if(parent.containsKey(currVertex) && neighbor == parent.get(currVertex)) {
//	                continue;
//	            }

			// If neighbor is not visited yet, then make it a child of currVertex 
			// in DFS tree and recur for it 
			if (!visited.contains(neighbor)) {
				children++;
				parent.put(neighbor, currVertex);
				APUtil(neighbor, visited, disc, low, parent, ap, adjacencyList);

				// Check if the subtree rooted with neighbor has a connection to 
				// one of the ancestors of currVertex 
				low.put(currVertex, Math.min(low.get(currVertex), low.get(neighbor)));

				// currVertex is an articulation point in following cases 

				// (1) currVertex is root of DFS tree and has two or more chilren. 
				// (2) If currVertex is not root and low value of one of its child is more than discovery value of currVertex.
				if (parent.get(currVertex) == null && children > 1 || parent.get(currVertex) != null && low.get(neighbor) >= disc.get(currVertex))
					ap.add(currVertex);

			}

			// Update low value of u for parent function calls. 
			else if (parent.containsKey(currVertex) && neighbor != parent.get(currVertex))
				low.put(currVertex, Math.min(low.get(currVertex), disc.get(neighbor)));
		}
	}
}

solutions.content:
The solution first tests how many islands are there.

-> If there is a single island, it resets every set bit and tests if resetting the bit disconnects the island. If it disconnects the single island into 2 or more islands OR removes a single bit island so that there are no islands left, we return "one day" as answer. 

-> If none of the bits tested could disconnect the single island, then we deduce that at least 2 bits are needed to do the job. It is provable that 2 bits can disconnect any island in any configuration. So, we return "2 days" as answer.

-> If in the first test, we find no islands or there are already disconnected islands, we simply return "0 days" as answer.

```
/*tktripathy*/
class Solution {
public:
    int dir[4][2] = { {-1, 0}, { 1, 0}, { 0, -1}, { 0, 1} };
    /* Accumulate adjacent ones to form an island */
    void dfs(vector<vector<int>>& g, int r, int c, vector<vector<bool>>& v, int rsz, int csz) 
    {
        v[r][c] = true;                
        for (auto d : dir) {
            int x = r + d[0], y = c + d[1];
            if ((x >= 0 && x < rsz) && (y >= 0 && y < csz) && (g[x][y] && !v[x][y]) )         
                dfs(g, x, y, v, rsz, csz);
        }
    }
    /* Count islands */
    int ci(vector<vector<int>>& g, int rsz, int csz) {
        int ic = 0; vector<vector<bool>> v(rsz, vector<bool>(csz, 0)); 
        for (int i = 0; i < rsz; i++) {
            for (int j = 0; j < csz; j++) {
                if (g[i][j] && !v[i][j]) { dfs(g, i, j, v, rsz, csz); ++ic; }
            }
        }
        return ic;
    }
    int minDays(vector<vector<int>>& g) {
        int single = 0, rsz = g.size(), csz = g[0].size();
        for (int i = 0; i < rsz; i++) {
            for (int j = 0; j < csz; j++) {
                if (g[i][j]) {
                    if (!single && ci(g, rsz, csz) == 1) single++; /* found a single island */
                    if (single) {       /* if so, reset every set bit and test if resetting */
                        g[i][j] = 0;    /* ... a bit might disconnect the island */
                        int r = ci(g, rsz, csz); if (r >= 2 /* disconnected */ || !r /* no island left */) return 1 /* one day */;
                        g[i][j] = 1;
                    } else return 0; /* no islands or already disconnected - zero days */
                }
            }
        }
        return 2; /* two days */
    }
};
