public class Solution {
    public int MinDays(int[][] grid) {
        int m = grid.Length;
        int n = grid[0].Length;
        int size = 0;
        for (int i = 0; i < m; i++)
        {
            for (int j = 0; j < n; j++) 
            {
                if (grid[i][j] == 1) 
                {
                    size++;
                }

                if (size > 1)
                {
                    break;
                }
            }

            if (size > 1) 
            {
                break;
            }
        }

        int islandCount = GetNumberOfIslands(grid);
        if (islandCount == 0 || islandCount > 1) 
        {
            return 0;
        }

        if (size == 1) 
        {
            return 1;
        }

        for (int i = 0; i < m; i++)
        {
            for (int j = 0; j < n; j++)
            {
                if (grid[i][j] == 1)
                {
                    grid[i][j] = 0;
                    if (GetNumberOfIslands(grid) > 1)
                    {
                        return 1;
                    }
                    grid[i][j] = 1;
                }
            }
        }

        return 2;
    }

    private int GetNumberOfIslands(int[][] grid)
    {
        int m = grid.Length;
        int n = grid[0].Length;
        bool[,] visited= new bool[m,n];
        List<int[]> dirs = new List<int[]>() {
            new int[2] { 0, 1},
            new int[2] { 0, -1},
            new int[2] { 1, 0}, 
            new int[2] { -1, 0}
        };

        int count = 0;
        for (int i = 0; i < m; i++)
        {
            for (int j = 0; j < n; j++) 
            {
                if (grid[i][j] == 1 && !visited[i,j])
                {
                    dfs(grid, i, j, visited, dirs);
                    count++;
                }
            }
        }

        return count;
    }

    private void dfs(int[][] grid, int i, int j, bool[,] visited, List<int[]> dirs)
    {
        visited[i, j] = true;
        foreach (int[] dir in dirs)
        {
            int nexti = i + dir[0];
            int nextj = j + dir[1];
            if (IsInBoundary(grid, nexti, nextj) && grid[nexti][nextj] == 1 && !visited[nexti, nextj])
            {
                dfs(grid, nexti, nextj, visited, dirs);
            }
        }
    }

    private bool IsInBoundary(int[][] grid, int i, int j)
    {
        return i >= 0 && j >= 0 && i < grid.Length && j < grid[0].Length;
    }
}
