import copy

# function that returns how many island in the given grid
def num_island(grid):
    neighbors = [[0, 1], [0, -1], [1, 0], [-1, 0]]
    def adjacent(x, y):
        return [[x+nx, y+ny] for nx, ny in neighbors if 0 <= x+nx < len(grid) and 0 <= y+ny < len(grid[0])]

    def recur(grid, r, c):
        if grid[r][c] == 0:
            pass

        grid[r][c] = 0

        neighbor = adjacent(r, c)

        for x, y in neighbor:
            if grid[x][y] == 1:
                recur(grid, x, y)
        return grid

    ret = 0
    for i, r in enumerate(grid):
        for j, c in enumerate(r):
            if c == 1:
                ret += 1
                grid = recur(grid, i, j)
    return ret

class Solution(object):
    def minDays(self, grid):
        grid_cpy = copy.deepcopy(grid)
        target = num_island(grid_cpy)

        # island is already diconnected
        if target >= 2 or target == 0:
            return 0

        # one island case
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    grid_cpy = copy.deepcopy(grid)
                    grid_cpy[i][j] = 0
                    if num_island(grid_cpy) == 2:
                        return 1
        return 2
