class Solution {
    int find(vector<int> &dp, int tar) {
        if (dp[tar] == tar) return tar;
        else {
            dp[tar] = find(dp, dp[tar]);
            return dp[tar];
        }
    }

    void merge(vector<int> &dp, int p, int q) {
        auto a = find(dp, p);
        auto b = find(dp, q);
        dp[b] = dp[a];
    }


    bool solve(const vector<vector<int>> &grid, int n, int m) {
        vector<int> dp(n * m, -1);
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j] == 1) dp[i * m + j] = i * m + j;
            }
        }
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j] == 1) {
                    if (i - 1 >= 0 && grid[i - 1][j] == 1) merge(dp, i * m + j, i * m - m + j);
                    if (i + 1 < n && grid[i + 1][j] == 1) merge(dp, i * m + j, i * m + m + j);
                    if (j - 1 >= 0 && grid[i][j - 1] == 1) merge(dp, i * m + j, i * m + j - 1);
                    if (j + 1 < m && grid[i][j + 1] == 1) merge(dp, i * m + j, i * m + j + 1);
                }
            }
        }
        int num = 0;
        for (int i = 0; i < n * m; ++i)
            if (dp[i] == i) num++;
        return num != 1;
    }

public:
    int minDays(vector<vector<int>> &grid) {
        int n = grid.size(), m = grid[0].size();
        bool ans = solve(grid, n, m);
        if (ans) return 0;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j] == 1) {
                    grid[i][j] = 0;
                    ans = solve(grid, n, m);
                    if (ans) return 1;
                    grid[i][j] = 1;
                }
            }
        }
        return 2;
    }
};
