class Solution {
    // deep copy the grid
    int[][] copy(int[][] grid){
        int[][] copy = new int[grid.length][grid[0].length];
        for(int i=0; i<grid.length; i++){
            copy[i] = new int[grid[0].length];
            System.arraycopy(grid[i], 0, copy[i], 0, grid[0].length);
        }
        return copy;
    }
    // make the whole island to 0
    void f(int[][] grid, int i, int j){
        if(i<0 || j<0 || i>=grid.length || j>=grid[0].length || grid[i][j]==0) return;
        grid[i][j]=0; 
        f(grid, i+1, j);
        f(grid, i-1, j);
        f(grid, i, j+1);
        f(grid, i, j-1);
    }
    // Check if the grid still connect. Make one island to 0 and check if still has 1.
    boolean connect(int[][] grid){
        boolean flag = false;
        for(int i=0; i<grid.length; i++)
            for(int j=0; j<grid[0].length; j++)
                if(grid[i][j]==1){
                    if(flag) return false;
                    f(grid, i, j);
                    flag=true;
                }
        return true;
    }

    public int minDays(int[][] grid) {
        int[][] copy = copy(grid);
        if(!connect(copy)) return 0; // case 1, already disconnect
        for(int i=0; i<grid.length; i++)
            for(int j=0; j<grid[0].length; j++)
                if(grid[i][j]==1){
                    copy = copy(grid);
                    copy[i][j]=0;
                    if(!connect(copy)) return 1; // case 2, set one cell to 0 then disconnct
                }
        return 2; // case 3, set 2 cells 0 then disconnect. You can ensure to make an edge cell disconnect by at most 2 
    }
}
