int minDays(vector<vector<int>>& grid)
{
	if(getIslandCount(grid) != 1)
	{
		return 0;
	}

	for(int row = 0; row < grid.size(); ++row)
	{
		for(int col = 0; col < grid[0].size(); ++col)
		{
			if(grid[row][col] == 1)
			{
				grid[row][col] = 0;

				if(getIslandCount(grid) != 1)
				{
					return 1;
				}

				grid[row][col] = 1;
			}
		}
	}

	return 2;
}

int getIslandCount(const vector<vector<int>>& grid)
{
	vector<vector<bool>> visited(grid.size(), vector<bool>(grid[0].size()));
	int count = 0;

	for(int row = 0; row < grid.size(); ++row)
	{
		for(int col = 0; col < grid[0].size(); ++col)
		{
			if(!visited[row][col] && grid[row][col] == 1)
			{
				dfs(grid, visited, row, col);
				++count;
			}
		}
	}

	return count;
}

void dfs(const vector<vector<int>>& grid, vector<vector<bool>>& visited, int row, int col)
{
	if(row < 0 || row >= grid.size() || col < 0 || col >= grid[0].size() || grid[row][col] != 1 || visited[row][col])
	{
		return;
	}

	visited[row][col] = true;

	dfs(grid, visited, row + 1, col);
	dfs(grid, visited, row - 1, col);
	dfs(grid, visited, row, col + 1);
	dfs(grid, visited, row, col - 1);
}
