class Solution {
public:
    vector<vector<bool>> vis;
    vector<int> x = {1, 0, -1, 0};
    vector<int> y = {0, -1, 0, 1};

    void dfs(int r, int c, vector<vector<int>>& grid){
        vis[r][c] = true;

        for(int i=0;i<4;i++){
            if(r+x[i]<0 || r+x[i]>=grid.size() || c+y[i]<0 || c+y[i]>=grid[0].size()) continue;
            if(vis[r+x[i]][c+y[i]] || !grid[r+x[i]][c+y[i]]) continue;
            dfs(r+x[i], c+y[i], grid);
        }
        return;
    }

    int helper(vector<vector<int>>& grid){
        int count = 0;
        int n = grid.size();
        int m = grid[0].size();
        vis.clear();
        vis.resize(n, vector<bool>(m, 0));

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j] || !grid[i][j]) continue;
                count++;
                dfs(i, j, grid);
            }
        } 

        return count;
    }

    int minDays(vector<vector<int>>& grid) {

        if(helper(grid)!=1) return 0;

        int n = grid.size();
        int m = grid[0].size();

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(!grid[i][j]) continue;
                grid[i][j] = 0;
                int k = helper(grid);
                if(k>1) return 1;
                grid[i][j] = 1;
            }
        }
        return 2;
    }
};
