class Solution {
    public int minDays(int[][] grid) {

        int m = grid.length, n = grid[0].length;

        int cnt = count(grid, m, n);
        if (cnt != 1) {

            return cnt == -1 ? 2 : 0;
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {

                if (grid[i][j] == 1) {

                    grid[i][j] = 0;

                    if (count(grid, m, n) > 1) {

                        return 1;
                    }
                    grid[i][j] = 1;
                }
            }
        }

        return 2;
    }

    private int countIsland(int[][] grid, int i, int j, int m, int n, 
                            boolean[][] visited) {

        if (i < 0 || j < 0 || i >= m || j >= n || visited[i][j] 
            || grid[i][j] == 0) return 0;

        visited[i][j] = true;

        return countIsland(grid, i, j + 1, m, n, visited) +
            countIsland(grid, i + 1, j, m, n, visited) +
            countIsland(grid, i - 1, j, m, n, visited) +
            countIsland(grid, i, j - 1, m, n, visited);
    }

    private int count(int[][] grid, int m, int n) {

        boolean[][] visited = new boolean[m][n];

        int count = 0, g = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {

                if (grid[i][j] == 1) {
                    ++g;
                    if (!visited[i][j]) {
                        countIsland(grid, i, j, m, n, visited);
                        ++count;
                    }
                }
            }   
        }

        if (g == (m * n)) {

            count = -1;
        }

        return count;
    }
}
