class UF {
    int[] parent;
    int[][] grid;
    UF(int n, int[][] g) {
        parent = new int[n];
        grid = g;
        for (int i = 0; i < n; ++i) {
            parent[i] = i;
        }
    }
    public int find(int v) {
        while (v != parent[v]) {
            parent[v] = parent[parent[v]];
            v = parent[v];
        }
        return v;
    }
    public void union(int k, int v) {
        parent[find(v)] = find(k);
    }
    public boolean multiIsland() {
        int t = -1;
        int m = grid.length;
        int n = grid[0].length;
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                if (grid[i][j] == 0) continue;
                if (t == -1) {
                    t = find(i*n+j);
                } else {
                    if (t!=find(i*n+j)) {
                        return true;
                    }
                }
            }
        }
        return false;
    }
}


class Solution {
    public boolean isOneIsland(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        UF uf = new UF(m*n,grid);
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                if (grid[i][j] == 0) continue;
                int cur = n*i+j;
                if (i-1>=0 && grid[i-1][j]==1) {
                    uf.union(cur, cur-n);
                }
                if (j-1>=0 && grid[i][j-1]==1) {
                    uf.union(cur, cur-1);
                }
            }
        }
        return !uf.multiIsland();
    }
    public int minDays(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        if (!isOneIsland(grid)) {
            return 0;
        }
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                if (grid[i][j] == 0) continue;
                grid[i][j] = 0;
                if (!isOneIsland(grid)) {
                    return 1;
                }
                grid[i][j] = 1;
            }
        }
        return 2;
    }
}
