from functools import lru_cache
class Solution:
    def numOfWays(self, nums: List[int]) -> int:
        @lru_cache(None)
        def combine(x, y):
            if x == 0 or y == 0: return 1
            return combine(x, y - 1) + combine(x - 1, y)

        bst = TreeNode(nums[0])
        for n in nums[1:]:
            bst.add(n)

        res = 1
        def traverse(root):
            nonlocal res
            if not root: return 0
            lc = traverse(root.left)
            rc = traverse(root.right)
            res = res * combine(lc, rc) % 1000000007
            return lc + rc + 1
        traverse(bst)

        return res - 1

class TreeNode:
    def __init__(self, v):
        self.val = v
        self.left = None
        self.right = None

    def add(self, v):        
        curr = self
        node = TreeNode(v)
        while 1:
            if v < curr.val:
                if not curr.left:
                    curr.left = node
                    break
                curr = curr.left
            else:                
                if not curr.right:
                    curr.right = node
                    break
                curr = curr.right
