class Solution {
public:
    vector < vector < long long > > pascal;
    int MOD = 1e9 + 7;

    int numOfWays(vector<int>& nums) {
        int N = nums.size();
        pascal = vector < vector < long long > > (N + 1);
        for (int i = 0; i < N + 1; i ++){
            pascal[i] = vector < long long > (i + 1, 1);
            for (int j = 1; j < i; j ++){
                pascal[i][j] = (pascal[i - 1][j] + pascal[i - 1][j - 1])%MOD;
            }
