class Solution {
    long mod = (int) 1e9 + 7;
    public int numOfWays(int[] nums) {
        List<Integer> list = new LinkedList<>();
        for(int i:nums){
            list.add(i);
        }

        return (int)(((helper(list)-1)%mod) % mod);
    }

    private long helper(List<Integer> list){
        if(list.size() == 0 || list.size() == 1) return 1;

        int root = list.get(0);
        List<Integer> bigger = new LinkedList<>();
        List<Integer> smaller = new LinkedList<>();

        for(int i:list){
            if(i > root){
                bigger.add(i);
            }
            if(i < root){
                smaller.add(i);
            }
        }
        int n = list.size();
        int left = smaller.size();
        long temp = (fac(n-1) / (fac(left)));
        temp = temp/fac(n-1-left);

        return (temp * helper(smaller) * helper(bigger));
    }

    private long fac(int n){
        long res = 1;
        while(n > 1){
            res = res * n;
            n--;
        }
        return res;
    }
}

