        if(nums == null || nums.size() <= 2) return 1;


        int root = nums.get(0);
        List<Integer> lessThan = new ArrayList<>();
        List<Integer> greaterThan = new ArrayList<>();

        for(int val : nums){
            if(val > root){
                greaterThan.add(val);
            } else if(val < root){
                lessThan.add(val);
            }
        }

        return ((((comb(lessThan.size() + greaterThan.size(), greaterThan.size()))%mod) * num(lessThan))%mod * num(greaterThan))%mod;
    }


    public long comb(int a, int b){
        if(a <= 0 || b <= 0) return 1;

        long ans = 1;
        for(int i = 1; i<=b;i++){
            ans = (ans * a)%mod;
            a--;
        }

        while(b>=1){
            ans = (ans * inverse[b--])%mod;
        }
        return ans;
    }



void inverseFunc(){
