class Solution(object):
    def numOfWays(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        self.ans = 1
        mod = 1000000007
        class BST:
            self.right = None
            self.left = None
            self.val = None
            self.below_count = None
            self.cont_ans = None

            def __init__(self,left=None,right=None,val=None):
                self.right = right
                self.left = left
                self.val = val

        def insert(root,val):
            if root.val == None:
                root.val = val
                return
            if val < root.val:
                if root.left == None:
                    root.left = BST(val=val)
                else:
                    insert(root.left,val)
            else:
                if root.right == None:
                    root.right = BST(val=val)
                else:
                    insert(root.right,val)

        def bel_count(root):
            if root is None:
                return 0
            root.below_count = 1
            root.below_count += bel_count(root.left)
            root.below_count += bel_count(root.right)
            return root.below_count

        def iot(root):
            if root is None:
                return
            iot(root.left)
            self.ans = (self.ans*root.cont_ans)%mod
            iot(root.right)


        def nck(n,k):
            if n<=0 or k<=0 or k>=n:
                return 1
            return factorial(n)/(factorial(k)*(factorial(n-k)))


        def get_ans(root,remain):
            if root is None:
                return
            root.cont_ans = 1
            root.cont_ans *= nck(remain,root.below_count)%mod
            root.cont_ans = root.cont_ans%mod
            get_ans(root.left,root.below_count-1)
            remain1 = root.below_count-1
            if root.left is not None:
                remain1 = remain1 - root.left.below_count
            get_ans(root.right,remain1)

        root = BST()
        for num in nums:
            insert(root,num)
        bel_count(root)
        get_ans(root,len(nums))
        iot(root)

        return self.ans-1


```

