class Solution:
    def numOfWays(self, nums: List[int]) -> int:

        # Time O(N^2)
        # Space O(N) for recursion stack

        def combine(n, k):

            def fac(n):
                s = 1
                for i in range(1, n + 1):
                    s *= i
                return s

            return fac(n) // (fac(n - k) * fac(k))


        def recur(nums):
            if len(nums) <= 2: 
                return 1
            left = []
            right = []
            for n in nums[1:]:
                if n < nums[0]:
                    left.append(n)
                elif n > nums[0]:
                    right.append(n)
            curr = combine(len(left)+len(right), len(right))
            return curr * recur(left) * recur(right)

        return (recur(nums)-1) % (10**9+7)

        """
        print all bst
        # Time O(2^N)
        # Space O(2^N)
        def get_all_bst(nums):
            candidates = []

            def recur(l, li, r, ri, temp):
                nonlocal candidates

                if len(temp) == len(l) + len(r):
                    candidates.append(temp[:])
                    return
                if li < len(l):
                    temp.append(l[li])
                    recur(l, li+1, r, ri, temp)
                    temp.pop()
