    def numOfWays(self, nums: List[int]) -> int:

        # Time O(N^2)
        # Space O(N) for recursion stack

        def combine(n, k):

            def fac(n):
                s = 1
                for i in range(1, n + 1):
                    s *= i
                return s

            return fac(n) // (fac(n - k) * fac(k))


        def recur(nums):
            if len(nums) <= 2: 
                return 1
