from math import comb
class Solution:
    def numOfWays(self, nums: List[int]) -> int:
        # each root must come before any of its children
        # recursive call on each root given nodes in subtree
        # for each tree - total number of nodes - 1 choose the number of nodes in one subtree
        # multiply by the result for both subtrees

        def recurse(nodes):
            if len(nodes) <= 2:
                return 1
            else:
                l = []
                r = []
                for val in nodes[1:]:
                    if val > nodes[0]:
                        r.append(val)
                    else:
                        l.append(val)
                # comb calculates nCk
                return comb(len(nodes) - 1, len(l)) * recurse(l) * recurse(r)

        return (recurse(nums) - 1) % 1000000007
