```
class Solution {
    // Pascal's Triangle is built only once per SUBMIT, and then can be 
    // used for all other test cases of the SUBMIT.
    private static int[][] pascalsTriangle = createPascalsTriangle(1001);
    private static final int MOD = 1_000_000_007;


    // Main method called by leetcode.
    public int numOfWays(int[] nums) {
        return dfs(nums, nums.length) - 1;
    }


    // Use an array representing a root followed by the values below that root, 
    // to calculate the number of combinations, by recursively breaking the 
    // array into smaller tree problems.
    private int dfs(int[] nums, int n) {
        if (n <= 2)  return 1;
        final int[] left = new int[n - 1];
        final int[] right = new int[n - 1];
        final int rootVal = nums[0];
        int leftIdx = 0;
        int rightIdx = 0;
        for (int i = 1; i < n; i++) 
            if (nums[i] < rootVal) 
                left[leftIdx++] = nums[i];
            else
                right[rightIdx++] = nums[i];
        return (int)(((((long)dfs(left, leftIdx) * dfs(right, rightIdx)) % MOD) * 
                pascalsTriangle[n - 1][leftIdx]) % MOD);
    }


    // Build table of Pascal's triangle values.  Only have to build 
    // it once per SUBMIT, then all test cases can use the same triangle.
    static private int[][] createPascalsTriangle(int maxN) {
        int[][] pt = new int[maxN][];
        for (int i = 0; i < maxN; i++) {
            pt[i] = new int[i + 1];
            pt[i][0] = pt[i][i] = 1;
            for (int j = 1; j < i; j++) 
                pt[i][j] = (pt[i - 1][j - 1] + pt[i - 1][j]) % MOD;
        }
        return pt;
    }
}
```

