const int mod = 1e9+7;

static int power(int x, int y) { return y == 0 ? 1 : (y % 2 == 0 ? power((long)x * x % mod, y / 2) : (long)x * power(x, y - 1) % mod); }
int inverse(int a) { return power(a, mod - 2); }

static int numerator(int i, int n) { return i <= n ? (long)numerator(i + 1, n) * i % mod : 1; }
static int denominator(int i, int k) { return i <= k ? (long)denominator(i + 1, k) * i % mod : 1; }
int c(int n, int k) { return (long)numerator(n - k + 1, n) * inverse(denominator(1, k)) % mod; }

static void count(int i, int n, int *cg, int *cl, int *nums) { i < n ? ((nums[i] > nums[0] ? (*cg)++ : (*cl)++), count(i+1, n, cg, cl, nums)) : (void)0; }
static void subs(int i, int n, int *g, int ig, int *l, int il, int *nums) { i < n ? ((nums[i] > nums[0] ? (g[ig++] = nums[i]) : (l[il++] = nums[i])), subs(i+1, n, g, ig, l, il, nums)) : (void)0; }
int numOfWays(int *nums, int numsSize)
{
    int *g, cg = 0, *l, cl = 0;
    int ans, i;

    return count(1, numsSize, &cg, &cl, nums),
        g = (cg > 0 ? calloc(cg, sizeof *g) : NULL),
        l = (cl > 0 ? calloc(cl, sizeof *l) : NULL),
        subs(1, numsSize, g, 0, l, 0, nums),
        ans = (long)c(numsSize-1, cg) * (cg > 2 ? numOfWays(g, cg) + 1 : 1) % mod * (cl > 2 ? numOfWays(l, cl) + 1 : 1) % mod,
        free(g),
        free(l),
        (ans - 1) % mod;
}   
```

