# Code
```
from math import comb

MOD = 10**9+7

class Solution:
    def numOfWays(self, nums: List[int]) -> int:
        root = self._build_bst(nums)
        return self.perms(root)[1] - 1

    def perms(self, node):
        """ returns (size, perms) """
        if node is None:
            return 0, 1

        left_size, left_perms = self.perms(node.left)
        right_size, right_perms = self.perms(node.right)
        size = left_size + right_size + 1

        res = comb(size-1, left_size) * left_perms * right_perms

        return size, res % MOD

    def _build_bst(self, nums):
        bst = BST()
        for x in nums:
            bst.insert(x)
        return bst.root


class BST:
    def __init__(self):
        self.root = None

    def insert(self, val):
        self.root = self._insert(self.root, val)

    def _insert(self, node, val):
        if node is None:
            return TreeNode(val)

        if val < node.val:
            node.left = self._insert(node.left, val)
        else:
            node.right = self._insert(node.right, val)
        return node


class TreeNode:
    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None
