I have calculated nCr using binary exponentiation and Class 12th nCr formula and Modular multiplicative inverse.

```
nCr= n! / r!*(n-r)!

class Solution {
public:
    long long fac[1001];
    int mod=1e9+7;
    void fact() {     // To calculate the Factorial and mod for value in range
        fac[0]=1;
        for(int i=1;i<=1000;i++) {
            fac[i]=1LL*fac[i-1]*i;
            fac[i]%=mod;
        }
    }
    long long bin(long long n, long long m) {   //Binary Exponentiaition (log n calculation)
        long long ans=1;
        while(m) {
            if(m&1) {
                ans*=n; ans%=mod;
            }
            n*=n; n%=mod;
            m/=2;
        }
        return ans;
    }
    long long ncr(int n, int k) {   // Calculating nCr using the above formula
        long long ans=fac[n];
        long long den=1LL*fac[k]*fac[n-k]%mod;
        return 1LL*(ans%mod*bin(den, mod-2)%mod)%mod;   
    }
    long long dfs(vector<int>&nums) {
        int n=nums.size();
        if(n<3) return 1;
        vector<int> left, right;
        for(int i=1;i<n;i++) if(nums[i]<nums[0]) left.push_back(nums[i]);
        else right.push_back(nums[i]);
        long long l1=dfs(left);
        long long r1=dfs(right);
        return 1LL*ncr(n-1, left.size())%mod*l1%mod*r1%mod;
    }
    int numOfWays(vector<int>& nums) {
        fact();
        return (int) dfs(nums)-1;
    }
};

