class Solution {
    int mod=1e9+7;
public:
    long long power(long long x,int y, int p)
    {
    long long res = 1; 
    x = x % p; 
    while (y > 0)
    {
        if (y & 1)
            res = (res * x) % p;
        y = y >> 1; 
        x = (x * x) % p;
    }
    return res;
}

long long modInverse(unsigned long long n,int p)
{
    return power(n, p - 2, p);
}

long long nCrModPFermat(long long n, int r, int p)
{
    if (n < r)
        return 0;
    if (r == 0)
        return 1;

    long long fac[n + 1];
    fac[0] = 1;
    for (int i = 1; i <= n; i++)
        fac[i] = (fac[i - 1] * i) % p;

    return (fac[n] * modInverse(fac[r], p) % p
            * modInverse(fac[n - r], p) % p)
           % p;
}
    long long lrbst(vector<int>& nums){
        int n=nums.size();
        if(n<=2) return 1;
        int root=nums[0];
        vector<int> left;
        vector<int> right;
        for(int i=1; i<n; i++){
            if(nums[i]<root) left.push_back(nums[i]);
            else right.push_back(nums[i]);
        }
        int l=left.size();
        int r=right.size();

        long long lb=lrbst(left);
        long long rb=lrbst(right);
        return ((lb%mod*rb%mod)%mod*nCrModPFermat(n-1,l,mod))%mod;
    }

public:
    int numOfWays(vector<int>& nums) {
        long long ans=lrbst(nums);
        return ans-1;
    }
};
