class Solution {
public:
    typedef long long int64;
    vector<vector<int64>> table;
    const int MOD = (int)1e9 + 7;

    // ways(nums) = ways(leftNodes) * ways(rightNodes) * (nums.size() - 1)C(leftNodes.size())
    // (nums.size() - 1)C(leftNodes.size()) = num of ways to keep leftNodes in same order keeping the root's position unchanged.

    int64 solve(vector<int> &nums) {
        int n = nums.size();
        if(n <= 2) {
            return 1;
        }
        vector<int> leftNodes, rightNodes;
        for(int i = 1; i < n; i++) {
            if(nums[i] < nums[0]) {
                leftNodes.push_back(nums[i]);
            } else {
                rightNodes.push_back(nums[i]);
            }
        }
        int64 leftWays = solve(leftNodes) % MOD;
        int64 rightWays = solve(rightNodes) % MOD;
        return ((leftWays * rightWays) % MOD) * table[n - 1][leftNodes.size()] % MOD;
    }

    int numOfWays(vector<int>& nums) {
        int n = nums.size();
        table.resize(n + 1); // pascal triangle
        for(int i = 0; i < (n + 1); i++) {
            table[i] = vector<int64> (i + 1, 1);
            for(int j = 1; j < i; j++) {
                // nCr + nCr-1 = n+1Cr
                table[i][j] = (table[i - 1][j] + table[i - 1][j - 1]) % MOD;
            }
        }

        return (solve(nums) - 1) % MOD;
    }
};
