        b[1] = BigInteger.ONE;
        int n = nums.length;
        if(n<=2) return 0;
        return (solve(nums).subtract(BigInteger.ONE)).mod(m).intValue();
    }
    public BigInteger solve(int[] nums){
        int n = nums.length;
        if(n<=2) return BigInteger.ONE;;
        int[] ar , br;
        int l = 0, h = 0;
        for(int i=1;i<nums.length;i++){
            if(nums[i]>nums[0]){
                h++;
            }else{
                l++;
            }
        }
        ar = new int[h];
        br = new int[l];
        h = 0;
        l = 0;
        for(int i=1;i<nums.length;i++){
            if(nums[i]>nums[0]){
                ar[h] = nums[i];
                h++;
            }else{
                br[l] = nums[i];
                l++;
            }
        }
        return ((b[n-1].divide(b[h]).divide(b[n-1-h]).mod(m).multiply(solve(ar)).multiply(solve(br)).mod(m))).mod(m);
    }
    public BigInteger factorial(int num){
    if (num<=1)
        return BigInteger.ONE;
    else
        return b[num] = factorial(num-1).multiply(BigInteger.valueOf(num));
    }
}
