def num_ways(nums)
    return @memo[nums] if @memo[nums]

    left = nums.select { |num| num < nums[0] }
    right = nums.select { |num| num > nums[0] }
    right = normalize(right)
    left_ways,right_ways = num_ways(left),num_ways(right)

    res = left_ways * right_ways
    res *= orderings(left.length,right.length)

    @memo[nums] = res % 1000000007
end

def orderings(a,b)
    fact(a+b) / fact(a) / fact(b)
end

def fact(n)
    return 1 if n < 2
    i = 1
    (2..n).each { |k| i *= k }
    i
end
