const MOD = 1000000007

func numOfWays(nums []int) int {
    cf := combFunc()
    return int((helper(nums, cf) - 1) % MOD)
}

func helper(n []int, comb func(x, y int64) int64) int64 {
    if len(n) <= 2 {
        return 1
    }
    left,right := []int{}, []int{}

    // Split elements compared to root (n[0])
    for _, el := range n {
        if el < n[0] {
            left = append(left, el)
        } else if el > n[0]{
            right = append(right, el)
        }
    }

    ll := int64(len(left))
    lr := int64(len(right))
    // 1. Find all combinations of possible arrangements of left and right
    //     without losing relative order 
    // 2. Find result for left and right elements recursively 
    // 3. Multiply all
    ways := ((comb(ll + lr, lr) * helper(left, comb)) % MOD) * helper(right, comb) 
    return ways % MOD
}

func combFunc() func(x, y int64) int64 {
    type item struct {
        x, y int64
    }
    var cache = map[item]int64{}
    var comb func(x, y int64) int64 
    // https://dotnettutorials.net/lesson/combination-formula-using-recursion-in-c/
    comb = func(x, y int64) int64 {
        if y == 0 || x == y {
            return 1
        }

        it := item{x, y}
        if cache[it] != 0 {
            return cache[it]
        }
        res := comb(x - 1, y - 1) + comb(x - 1, y)
        cache[it] = res % MOD
        return cache[it]
    }

    return comb
}


