class Solution {
public:
    const int mod = 1e9 + 7;

    long long getNumOfWays(vector<int>& nums, vector<vector<long long>>& comb){
        if(nums.size() <= 2){
            return 1;
        }

        vector<int> less;
        vector<int> more;

        int x = nums[0];
        for(int i = 1; i < nums.size(); i++){
            if(nums[i] < x){
                less.push_back(nums[i]);
            }
            else{
                more.push_back(nums[i]);
            }
        }

        long long ans = comb[more.size() + less.size()][less.size()];
        ans *= getNumOfWays(less, comb);
        ans %= mod;
        ans *= getNumOfWays(more, comb);
        ans %= mod;
        return ans;
    }

    int numOfWays(vector<int>& nums) {
        int i, j, n = nums.size();        
        vector<vector<long long>> comb(n + 1, vector<long long> (n + 1));

        for(i = 0; i <= n; i++){
            comb[i][0] = 1;
        }

        for(i = 1; i <= n; i++){
            for(j = 1; j <= i; j++){
                comb[i][j] = (comb[i - 1][j] + comb[i - 1][j - 1]) % mod;
            }
        }

        return getNumOfWays(nums, comb) - 1;
    }
};
