#define ll long long int

const ll mod = 1e9+7;
vector<ll> fac, sz, dp;
vector<vector<int>> adj;

struct Node{
    int data;
    Node *left = NULL, *right = NULL;
    Node(int val):data(val){}
};
class Solution {
public:
    ll power(ll x, ll y){
        x %= mod;
        if(!x)  return x;
        ll res = 1;
        while(y){
            if(y&1)
                res = (res*x)%mod;
            y >>= 1;
            x = (x*x)%mod;
        }
        return res;
    }
    ll inverse(ll x){
        return power(x, mod-2);
    }
    Node* insert(Node *root, int val){
        if(!root){
            Node *tmp = new Node(val);
            return tmp;
        }
        if(root->data < val)
            root->right = insert(root->right, val);
        else
            root->left = insert(root->left, val);
        return root;
    }
    void inorder(Node *root, Node *par){
        if(!root)
            return;
        inorder(root->left, root);
        inorder(root->right, root);
        if(par != NULL){
            sz[par->data] += sz[root->data];
        }
        ll val = fac[sz[root->data]-1];
        if(root->left != NULL){
            val = (val*inverse(fac[sz[root->left->data]]))%mod;
            dp[root->data] = (dp[root->data]*dp[root->left->data])%mod;
        }
        if(root->right != NULL){
            val = (val*inverse(fac[sz[root->right->data]]))%mod;
            dp[root->data] = (dp[root->data]*dp[root->right->data])%mod;
        }
        dp[root->data] = (dp[root->data]*val)%mod;
    }

    int numOfWays(vector<int>& nums) {
        int n = nums.size();
        fac.assign(n+10,1);
        for(int i = 2; i <= n; i++)
            fac[i] = (fac[i-1]*i)%mod;
        sz.assign(n+10,1);
        dp.assign(n+10,1);
        adj.assign(n+1,vector<int>());
        Node *root = new Node(nums[0]);
        for(int i = 1; i < n; i++)
            insert(root, nums[i]);
        inorder(root, NULL);
        dp[nums[0]]--;
        dp[nums[0]] += mod;
        dp[nums[0]] %= mod;
        return dp[nums[0]];
    }
};
