class Solution:
    def build(self,dp,n):
        for i in range(n+1):
            dp[i][0]=1
            dp[i][i]=1
            dp[i][1]=i
            dp[i][i-1]=i

        for i in range(4,n+1):
            for j in range(2,i):
                dp[i][j]= dp[i-1][j-1]+dp[i-1][j]     
    def helper(self,nums,dp):
        n=len(nums)
        if n<=2:
            return 1
        left=[]
        right=[]
        root=nums[0]
        left=[n for n in nums if n < root]
        right=[n for n in nums if n> root]
        countLeft = self.helper(left,dp)
        countRight = self.helper(right,dp)
        return (dp[len(left)+len(right)][len(left)] *  countRight * countLeft    ) 

    def numOfWays(self, nums) -> int:
        n=len(nums)
        dp=[[0 for i in range(n+1)]for j in range(n+1)]
        self.build(dp,n) #used to buil dp matrix
        ans=self.helper(nums,dp)          
        return ans % (10**9+7)-1
