public class Solution {
    public int NumOfWays(int[] nums) {
         return (int)(numOfWaysHelper(nums) % (1_000_000_007)) - 1;
    }
    BigInteger numOfWaysHelper (int[] nums) {
        if(nums.Length < 3)
            return 1;

        var root  = nums[0];
        var left  = nums.Where(p => p < root).ToArray();
        var right = nums.Where(p => p > root).ToArray();

        return comb(right.Count() + left.Count(), right.Count()) * numOfWaysHelper(left) * numOfWaysHelper(right);
    }

    BigInteger comb(BigInteger n, BigInteger k){
        if(n < 2)
            return 1;
        return fact(n)/fact(n-k)/fact(k);
    }

    BigInteger fact(BigInteger n){
        if(n<2)
            return 1L;

        var result = n * fact(n-1);
        return result;
    }
}
