    def numOfWays(self, nums: List[int]) -> int:
        def quicksort(arr):
            if(len(arr) <= 1 or len(arr) == 2):
                return 1
            pivot = arr[0]
            left = []
            right = []
            for i in range(1, len(arr)):
                if(arr[i] < pivot):
                    left.append(arr[i])
                else:
                    right.append(arr[i])
            l = len(left)
            r = len(right)
            ans = comb(l+r,l);
            return ((ans)*quicksort(left)*quicksort(right))


        return (quicksort(nums)% (10**9 + 7)) -1

