type TreeNode struct {
    Val int
    SizeLeft int
    SizeRight int
    Left *TreeNode
    Right *TreeNode
}

func insert(node *TreeNode, val int){
    if val < node.Val{
        if node.Left == nil{
            node.Left = &TreeNode{Val : val}
        } else {
            insert(node.Left, val)
        }
        node.SizeLeft++
    }else{
        if node.Right == nil{
            node.Right = &TreeNode{Val : val}
        } else {
            insert(node.Right, val)
        }
        node.SizeRight++
    }
}

func solve(node *TreeNode, dp *[]int, ncr *[][]int, mod int) int{
    if node == nil {
        return 1
    }
    if (*dp)[node.Val] != -1 {
        return (*dp)[node.Val]
    }
    waysLeft := solve(node.Left, dp, ncr, mod)
    waysRight := solve(node.Right, dp, ncr, mod)
    nodesLeft := node.SizeLeft
    nodesRight := node.SizeRight
    sol := waysLeft
    sol = (sol * waysRight) % mod
    sol = (sol * (*ncr)[nodesLeft + nodesRight] [nodesLeft]) % mod
    (*dp)[node.Val] = sol
    return sol
}

func numOfWays(nums []int) int {
    if len(nums) == 0 {
        return 0
    }
    const mod = int(1e9 + 7)
    root := &TreeNode{Val : nums[0]}
    for i := 1; i < len(nums); i++ {
        insert(root, nums[i])
    }
    dp := make([]int, len(nums) + 1)
    for i := range dp{
        dp[i] = -1
    }
    ncr := make([][]int, 1005)
    for i := 0; i <= 1000; i++ {
        ncr[i] = make([]int, i + 1)
        ncr[i][0] = 1
        ncr[i][i] = 1
        for j := 1; j < i; j++ {
            ncr[i][j] = (ncr[i - 1][j] + ncr[i - 1][j - 1]) % mod
        }
    }
    return (solve(root, &dp, &ncr, mod) - 1 + mod) % mod;
}
