#define ll long long

class Solution {
public:

    ll MOD=1e9L+7;
    ll dp[1001][1001]; // dp[i][j] = iCj (i choose j)

    ll util(vector<int>& v){
        int n=v.size();
        if(n==0) return 1; // base case when binary search tree is empty

        int root=v[0]; // root of the binary search tree

        vector<int> left, right;

        for(int i=1; i<n; i++){
            if(v[i]>root){
                right.push_back(v[i]); // right children of root
            }
            else if(v[i]<root){
                left.push_back(v[i]); // left children of root
            }
        }

        ll left_ans=util(left); // number of ways to form left subtree
        ll right_ans=util(right); // number of ways to form right subtree

        ll ans=(ll)left_ans*right_ans%MOD; // merging the answer of left and right (standard divide conquer strategy)

        ans=(ll)ans*dp[n-1][(int)left.size()]%MOD; // also among total n-1 insertions in this tree there are (n-1)C(left.size()) ways to interleave the left and right insertions

        return ans;
    }

    int numOfWays(vector<int>& nums) {
        dp[0][0]=1;

        for(ll i=1; i<=nums.size(); i++){
            dp[i][0]=1;
            dp[i][i]=1;
            for(ll j=1; j<i; j++){
                dp[i][j]=(dp[i-1][j-1]+dp[i-1][j])%MOD;
            }
        }

        ll ans=util(nums);

        ans=(ans+MOD-1)%MOD; // need to subtract the already given ordering

        return ans; // :)
    }
};
