// any sequence of size (x+y) can give the same BST if the mutual ordering of the permutation from set A and permutation of set B is maintained. There are choose(x + y, y) ways to do that.

class Solution {
public:
    #define mod 1000000007

   vector<vector<int>> comb; 

    void combs(int n){

        for(int i=2;i<=n;i++){
            for(int j=1;j<i;j++){
                comb[i][j] = (comb[i-1][j]+comb[i-1][j-1])%mod;
            }
        }
        return;
    }


    int helper(vector<int>& nums){

        int n = nums.size();
        if(n<=2) return 1;
        int l = 0, r = 0;
        vector<int> left, right;
        int root = nums[0];

        for(int x:nums){
            if(x<root){ left.push_back(x); l++;}
            if(x>root){ right.push_back(x); r++;}
        }
        long long res = 1;
        res = (res*(comb[l+r][r]%mod))%mod;
        res = (res*(helper(left)%mod))%mod;
        res = (res*(helper(right)%mod))%mod;
        return res%mod;
    }

    int numOfWays(vector<int>& nums) {
        int n = nums.size();
        comb.resize(n+1, vector<int>(n+1, 1));
        combs(n);

        return (helper(nums)-1)%mod;
    }
};
```

