             public class Solution
        {
            private class Node
            {
                public int Val;
                public Node Left;
                public Node Right;
                public int SubTreeSize;
            }


            private const int MODULO = 1_000_000_007;

            private void FillSubtreeSize(Node node)
            {
                if (node == null)
                {
                    return;
                }

                node.SubTreeSize++;
                FillSubtreeSize(node.Left);
                FillSubtreeSize(node.Right);
                node.SubTreeSize += (node.Left?.SubTreeSize).GetValueOrDefault(0);
                node.SubTreeSize += (node.Right?.SubTreeSize).GetValueOrDefault(0);
            }

            private BigInteger Factorial(long n, BigInteger?[] factorialCache)
            {
                if (n == 0)
                {
                    return 1;
                }

                if (factorialCache[n].HasValue)
                {
                    return factorialCache[n].Value;
                }

                var next = Factorial(n - 1, factorialCache);
                var res = next * n;
                factorialCache[n] = res;
                return res;
            }

            private long CnK(long n, long k, BigInteger?[] factorialCache)
            {
                checked
                {
                    var cnk = Factorial(n, factorialCache) /
                              (Factorial(k, factorialCache) * Factorial(n - k, factorialCache));
                    cnk %= MODULO;
                    return (long) cnk;
                }
            }

            private long Helper(Node node, BigInteger?[] factorialCache)
            {
                if (node.Left == null && node.Right == null)
                {
                    return 1;
                }

                if (node.Left == null && node.Right != null)
                {
                    return Helper(node.Right, factorialCache);
                }

                if (node.Left != null && node.Right == null)
                {
                    return Helper(node.Left, factorialCache);
                }

                var l = Helper(node.Left, factorialCache);
                var r = Helper(node.Right, factorialCache);

                long intersections = l * r;
                intersections %= MODULO;
                intersections *= CnK(node.SubTreeSize - 1, node.Left.SubTreeSize,
                    factorialCache);
                intersections %= MODULO;
                return intersections;
            }

            public int NumOfWays(int[] nums)
            {
                Node root = new Node();
                root.Val = nums[0];

                for (int i = 1; i < nums.Length; i++)
                {
                    var val = nums[i];
                    Node node = root;

                    while (true)
                    {
                        if (val < node.Val)
                        {
                            if (node.Left == null)
                            {
                                node.Left = new Node();
                                node.Left.Val = val;
                                break;
                            }
                            else
                            {
                                node = node.Left;
                            }

                            continue;
                        }


                        if (node.Right == null)
                        {
                            node.Right = new Node();
                            node.Right.Val = val;
                            break;
                        }
                        else
                        {
                            node = node.Right;
                        }
                    }
                }

                FillSubtreeSize(root);
                var res = Helper(root, new BigInteger?[nums.Length + 1]);
                res--;
                res += MODULO;
                res %= MODULO;
                return (int)res;
            }
        }
