class Solution {
private:
    vector<int> fact, invf;
    int p = 1e9 + 7;
public:
    int mul(int x, int y, int p) {
        long long ans = x;
        ans *= y;
        ans %= p;
        return (int) ans;
    }

    int pow(int x, int y, int p) {
        int ans = 1, t = x;
        while (y > 0) {
            if (y & 1) ans = mul(ans, t, p);
            y >>= 1;
            t = mul(t, t, p);
        }
        return ans;
    }

    int inv(int x, int p) {
        return pow(x, p - 2, p);
    }

    int C(int n, int r, int p) {
        return mul(fact[n], mul(invf[r], invf[n - r], p), p);
    }

    int numOfWays(vector<int>& nums) {
        fact = vector<int>(1002), invf = vector<int>(1002);
        for (int i = 0; i < fact.size(); i++) {
            if (i < 2) fact[i] = 1;
            else fact[i] = mul(i, fact[i - 1], p);
            invf[i] = inv(fact[i], p);
        }
        return calc(nums) - 1;
    }

    int calc(vector<int> nums) {
        int root = nums[0];
        vector<int> l, r;
        for (int num: nums) {
            if (num < root) l.push_back(num);
            else if (num > root) r.push_back(num);
        }
        int ans = C(l.size() + r.size(), l.size(), p);
        if (l.size() != 0) ans = mul(ans, calc(l), p);
        if (r.size() != 0) ans = mul(ans, calc(r), p);
        return ans;
    }
};
