    }
    long long modInverse(long long val2, long long mod){
        long long xx, yy; 
        long long g = gcdExtended(val2, mod, &xx, &yy);
        if (g != 1)return -1;
        return (xx % mod + mod) % mod;
    }
    long long gcdExtended(long long val1, long long val2,long long* xx,long long* yy){
        if (val1 == 0) {
            *xx = 0, *yy = 1;
            return val2;
        }
        long long x1, y1;
        long long gcd = gcdExtended(val2 % val1, val1, &x1, &y1);
        *xx = y1 - (val2 / val1) * x1;
        *yy = x1;
        return gcd;
    }
    long long modDivide(long long val1, long long val2,long long mod){
        val1 = val1 % mod;
        long long inv = modInverse(val2, mod);
        if (inv == -1)
            return 0;
        else
            return (inv * val1) % mod;
    }
    int nCr(int n, int r, int x){
        if (r > n)
            return 0;
        if (r > n - r)
            r = n - r;
        long long ans = 1;
        for (int i = 1; i <= r; i++) {
            ans = moduloMultiplication(ans, (n + 1 - i), x);
            ans = modDivide(ans, i, x);
        }
        return ans;
    }
public:
    int numOfWays(vector<int>& nums) {
        int n = nums.size();
        long long ans = 1;
        set <int> s;
        s.insert(0); s.insert(n+1);
        for (int i = 0; i < n; i++){
            auto it = s.lower_bound(nums[i]);
            int y = *(it--), x = *it;
            //cout << i << " " << y-x-2 << " " << nums[i] - x - 1 << " " << nCr(y-x-2,nums[i]-x-1,N) << endl;
            ans = (ans * nCr(y-x-2, nums[i]-x-1, N))%N;
            s.insert(nums[i]);
        }
        return ans-1;
    }
};
```

