class Solution {
    const int mod = 1e9+7;
    #define ll long long
    ll power(ll a,ll b){
        if(b<=0){
            return 1;
        }
        a = a%mod;
        ll ans=1;
        while(b){
            if(b%2==1){
                ans = ans*1LL*a;
                ans = ans%mod;
            }
            a = (a*1LL*a)%mod;
            b = (b>>1);
        }
        return ans%mod;
    }

    ll fact[1001];
    void pre(){
        fact[0]=1;
        for(int i=1;i<1001;i++){
            fact[i] = (fact[i-1]*1LL*i)%mod;
        }
    }

    ll ncr(ll n, ll r){
        if(r>=n){
            return 1;
        }
        ll nu = fact[n]%mod;
        ll de = power((fact[r]*1LL*fact[n-r])%mod,mod-2)%mod;
        nu  = (nu*1LL*de)%mod;
        return nu;
    }

    int cal(vector<int> &v,ll &ans){
        if(v.size()<=0){
            return 0;
        }
        int node = v[0];
        vector<int> left,right;
        for(int j=1;j<v.size();j++){
            if(v[j]>node){
                right.push_back(v[j]);
            }
            else{
                left.push_back(v[j]);
            }
        }
        int l = cal(left,ans);
        int r = cal(right,ans);
        ans = (ans*1LL*(ncr(l+r,l)))%mod;
        // cout<<ans<<
        return l+r+1;
    }


public:
    int numOfWays(vector<int>& nums) {
        pre();
        ll ans=1;
        // int i=0;
        cal(nums,ans);
        return ans-1;
    }
};
