        vector<int> left, right;
        for (int i=1; i<m; i++) {
            // constructing right and left subtrees for bst with root as nums[0]
            if (nums[i] < nums[0]) left.push_back(nums[i]);
            else right.push_back(nums[i]);
        }
        long long waysinleft = dfs(left)%mod;
        long long waysinright = dfs(right)%mod;
        // m-1 C left.size() to get binomial coefficient
        return (((waysinleft*waysinright)%mod)*pascal[m-1][left.size()])%mod;
