class Solution {
    static int[][] combinations = fillCombinations();
    static final int MOD = 1000000007;

    public int numOfWays(int[] nums) {
        int answer = ways(nums)%MOD;
        answer--;
        return answer;
    }

    public int ways(int[] nums) {
        int size = nums.length;
        if(size==0) {
            return 1;
        }
        int count = 0;
        int target = nums[0];
        for(int i=0; i<size; i++) {
            if(nums[i]<target) {
                count++;
            }
        }
        int[] less = new int[count];
        count = 0;
        for(int i=0; i<size; i++) {
            if(nums[i]<target) {
                less[count] = nums[i];
                count++;
            }
        }
        count = 0;
        for(int i=0; i<size; i++) {
            if(nums[i]>target) {
                count++;
            }
