int mod = 1000000007;

const int MOD = 1e9 + 7;

int inverse(int a, int m)
{
    int m0 = m, t, q;
    int x0 = 0, x1 = 1;
    if (m == 1)
        return 0;
    while (a > 1) {
        q = a / m;
        t = m;
        m = a % m, a = t;
        t = x0;
        x0 = x1 - q * x0;
        x1 = t;
    }
    if (x1 < 0)
        x1 += m0;
    return x1;
}

int binomialCoeff(int n, int k)
{
    long long res = 1;
    if (k > n - k)
        k = n - k;
    for (int i = 0; i < k; ++i)
    {
        res = (res * (n - i)) % MOD;
        res = (res * inverse(i + 1, MOD)) % MOD;
    }
    return (int)res;
}



int mul(int a, int b, int c)
{
    long long res = (long long)a * b % MOD;
    res = res * c % MOD;
    return (int)res;
}

int calculate(int m , int n){

    int a = max(m,n);
    int b = min(m,n);

    int num = 1;
    int den = 1;

    for(int i = 1 ; i <= b ; i++) {

        num *= a+i;
        den *= i;
    }

    return num/den ;
}

int solve(vector<int>& nums , int n){

    // base case

    if( n <= 2) return 1;

    vector<int>small;
    vector<int>large;

    int root = nums[0];

    for(int i = 1 ; i < n ; i++){

        if( nums[i] < root) small.push_back(nums[i]);

        else large.push_back(nums[i]);
    }

    int left_per = solve(small , small.size());
    int right_per = solve(large , large.size());

    int m_n = small.size()+large.size();
    int m = large.size();


    return mul(binomialCoeff(m_n , m) ,left_per,right_per);


}

class Solution {
public:
    int numOfWays(vector<int>& nums) {

        return solve(nums , nums.size())-1;

    }
};
```

