//PLEASE_UPVOTE
class Solution {
public:
    int mod=1e9+7;
    int func(int n,int k){
        //this function is store the binomial coefficient
        vector<vector<long long>> dp(n + 1, vector<long long>(k + 1, 0));
        dp[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            dp[i][0] = 1;
            for (int j = 1; j <= min(i, k); j++) {
                dp[i][j] = (dp[i - 1][j - 1] + dp[i - 1][j])%mod;
            }
        }

        return dp[n][k];
    }
    long long solve(vector<int>&nums){
        if(nums.size()<=1)return 1;
        int n=nums.size();
        vector<int>smaller,greater;
        for(int i=1;i<n;i++){
            if(nums[i]>nums[0])greater.push_back(nums[i]);
            else smaller.push_back(nums[i]);
        }
        int m=smaller.size();
        long long ans=func(n-1,m);
        //n-1 is passed in the ans as total size will be 1 minus from root i.e. nums[0].
        //smaller is meant for left subtree
        //greater is meant for right subtree
        return ans%mod*solve(smaller)%mod*solve(greater)%mod;
    }
    int numOfWays(vector<int>& nums) {
        return (solve(nums)-1)%mod;//-1 to remove the count of original array.
    }
};
