class Solution {

    private static final int MOD = 1000000007;
    private long[][] PT;
    public int numOfWays(int[] nums) {
        int n = nums.length;

        PT = new long[n+1][n+1];
        for(int i = 0; i <= n; i++) {
            PT[i][0] = PT[i][i] = 1;
        }

        for(int i = 2; i <= n; i++) {
            for(int j = 1; j < i; j++) {
                PT[i][j] = (PT[i-1][j] + PT[i-1][j-1])%MOD;
            }
        }

        ArrayList<Integer> arr = new ArrayList<>();
        for(int val : nums) {
            arr.add(val);
        }
        return ((int)(solve(arr)-1))%MOD;
    }

    private long solve(ArrayList<Integer> nums) {
        int n = nums.size();
        if(n < 3) {
            return 1;
        }

        ArrayList<Integer> left = new ArrayList<>();
        ArrayList<Integer> right = new ArrayList<>();
        int root = nums.get(0);

        for(int i = 1; i < n; i++) {
            int node = nums.get(i);
            if(node < root) {
                left.add(node);
            } else {
                right.add(node);
            }
        }

        long x = solve(left)%MOD;
        long y = solve(right)%MOD;

        long z = PT[n-1][left.size()];
        return (((x*y)%MOD)*z)%MOD;
    }

} 
