Easy C++ clean code, with logic:

```
class Solution {
public:


    /*
        Logic:
        [3,4,5,1,2]

        intuition: - 3 is root
                   - 1 then 2 
                   - 4 then 5
        => array will look like  3 .. 1 4 .. 4 5

        Now the logic to find these numbers is simple
        3 _ _ _ _ 
        We want to find the no of ways 1,2,4,5 can fit these 4 dash, with 1 then 2, and 4 then 5

        simple find the no of ways to fill 1 and 2, then fill the remaining 4 and 5 in the remain

        thus no of ways will be 4C2 in this case

        Now the left tree can have multiple ways to be constructed, same for right tree

        thus result at one root is, left * right * nCk
    */

    const int MOD = 1e9 + 7;
    #define ll long long

    int binpower(ll base, ll p) {
        ll res = 1;
        while(p > 0) {
            if(p & 1) {
                res = mul(res, base);
            }
            p >>= 1;
            base = mul(base, base);
        }
        return res;
    }

    int modInv(ll oper) {
        return binpower(oper, MOD-2);
    }

    int mod(ll val) {
        return ((val % MOD) + MOD) % MOD;
    }

    int mul(ll oper1, ll oper2) {
        oper1 = mod(oper1);
        oper2 = mod(oper2);
        ll res = oper1 * oper2;
        res = mod(res);
        return res;
    }

    int div(ll oper1, ll oper2) {
        oper1 = mod(oper1);
        oper2 = mod(oper2);
        ll res = oper1 * modInv(oper2);
        res = mod(res);
        return res;
    }

    vector<int> fac;

    void init(int n) {
        fac = vector<int>(n+1);
        fac[0] = 1;
        for(int i = 1; i <= n; i++) {
            fac[i] = mul(fac[i-1], i);
        }
    }

    int nCr(int n, int r) {
        int num = fac[n];
        int den = mul(fac[r], fac[n-r]);
        int res = div(num, den);
        return res;
    }

    int numOfWaysUtil(vector<int> nums) {
        int n = nums.size();
        if(n == 0) return 1;

        int root = nums[0];
        vector<int> smaller, greater;
        for(auto it: nums) {
            if(it < root) smaller.push_back(it);
            if(it > root) greater.push_back(it);
        }

        int left = numOfWaysUtil(smaller);
        int right = numOfWaysUtil(greater);

        int sz = smaller.size();
        int res = mul(mul(left, right), nCr(n-1, sz));
        return res;
    }

    int numOfWays(vector<int> nums) {
        int n = nums.size();
        init(n);
        int res = numOfWaysUtil(nums) - 1;
        return res;
    }
};
```

